mapper.update:
UPDATE sys_user SET username = #{username}, real_name = #{realName}, phone = #{phone}, email = #{email},
password = #{password}, salt = #{salt},
update_time = #{updateTime} WHERE id = #{id}

mapper.delete:
UPDATE sys_user
SET is_deleted = 1
WHERE id = #{id}

mapper.update:
