mapper.select:
SELECT

FROM sys_operation_log
WHERE 1=1

    AND (operator LIKE CONCAT('%', #{keyword}, '%') 
         OR action LIKE CONCAT('%', #{keyword}, '%'))


    AND DATE(create_time) >= #{startDate}


    AND DATE(create_time) <= #{endDate}

ORDER BY create_time DESC

    LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM sys_operation_log WHERE 1=1
AND (operator LIKE CONCAT('%', #{keyword}, '%') OR action LIKE CONCAT('%', #{keyword}, '%'))
AND DATE(create_time) >= #{startDate}
AND DATE(create_time) <= #{endDate}
