mapper.insert:
INSERT INTO sys_user (
    id, username, password, salt, phone, nickname, email,
    status, status_str, balance, points, level, create_time, update_time
) VALUES (
             #{id}, #{username}, #{password}, #{salt}, #{phone}, #{nickname}, #{email},
             #{status}, #{statusStr}, #{balance}, #{points}, #{level}, #{createTime}, #{updateTime}
         )

mapper.update:
UPDATE sys_user
nickname = #{nickname}, avatar_url = #{avatarUrl}, phone = #{phone}, email = #{email}, gender = #{gender}, bio = #{bio}, real_name = #{realName}, status = #{status}, status_str = #{statusStr}, password = #{password}, salt = #{salt}, update_time = #{updateTime}
WHERE id = #{id}

UPDATE sys_user
SET status = #{status}
WHERE id = #{userId}

UPDATE sys_user
SET status_str = #{statusStr}
WHERE id = #{userId}

UPDATE sys_user
SET password = #{password},
    salt = #{salt}
WHERE id = #{userId}

mapper.select:
SELECT

FROM sys_user
WHERE is_deleted = 0

    AND create_time >= #{startTime}


    AND create_time <= #{endTime}


    AND id > #{lastId}

ORDER BY id ASC

    LIMIT #{limit}

SELECT COUNT(*)
FROM sys_user
WHERE is_deleted = 0

    AND create_time >= #{startTime}


    AND create_time <= #{endTime}
