mapper.select:
SELECT
FROM sys_user WHERE is_deleted = 0
AND (username LIKE CONCAT('%', #{keyword}, '%') OR phone LIKE CONCAT('%', #{keyword}, '%') OR CAST(id AS CHAR) LIKE CONCAT('%', #{keyword}, '%'))
AND status_str = #{status}
AND level = #{level}
ORDER BY create_time DESC

mapper.delete:
UPDATE sys_user
SET is_deleted = 1
WHERE id = #{id}

mapper.update:
UPDATE sys_user
SET status = #{status}
WHERE id = #{userId}

UPDATE sys_user
SET password = #{password},
    salt = #{salt}
WHERE id = #{userId}
