mapper.update:
UPDATE video_info
title = #{title}, description = #{description}, cover_url = #{coverUrl}, view_count = #{playCount}, like_count = #{likeCount}, comment_count = #{commentCount},
WHERE id = #{id}

mapper.select:
SELECT  FROM video_info
WHERE status = CASE #{status} WHEN 'PENDING' THEN 0 WHEN 'PASSED' THEN 1 WHEN 'REJECTED' THEN 2 ELSE 0 END
ORDER BY view_count DESC LIMIT #{limit}

