mapper.select:
SELECT

FROM video_play_record
WHERE 1=1

    AND create_time >= #{startTime}


    AND create_time <= #{endTime}


    AND id > #{lastId}

ORDER BY id ASC

    LIMIT #{limit}

SELECT COUNT(*)
FROM video_play_record
WHERE 1=1

    AND create_time >= #{startTime}


    AND create_time <= #{endTime}

SELECT

FROM video_play_record
WHERE user_id = #{userId}
ORDER BY create_time DESC

    LIMIT #{limit}

SELECT

FROM video_play_record
WHERE (user_id, video_id, create_time) IN (
SELECT user_id, video_id, MAX(create_time) as max_time
FROM video_play_record
WHERE user_id = #{userId}
GROUP BY user_id, video_id
)
ORDER BY create_time DESC

    LIMIT #{limit}

mapper.update:
UPDATE video_play_record
SET duration = #{duration},
    progress = #{progress},
    is_finish = #{isFinish},
    create_time = #{createTime}
WHERE id = #{id}
